mapper.update:
update work
set regdte = #{regdte},
	enddte = #{enddte},
	cont = #{cont}
WHERE WORKNO = #{WORKNO}

mapper.select:
SELECT *
FROM FILES f
WHERE FNO = #{FNO}

SELECT *
from project p
where prjno=#{prjno}

SELECT DISTINCT OWNER 
FROM GANTT
WHERE PRJNO = #{prjno}

SELECT g.*, to_char(to_date(g.START_DATE,'yyyy-mm-dd') + g.duration,'yyyy-mm-dd') AS end_date
FROM GANTT g
WHERE id = #{id}

mapper.update:
UPDATE gantt 
	SET progress=#{progress}
WHERE id=#{id}
